import React from "react";
import { Heading } from "../components/Heading";
import { AppInput } from "../components/UI/AppInput";
import { AppCheckbox } from "../components/UI/AppCheckbox";
import { Button } from "../components/Button";
import { Controller, useForm } from "react-hook-form";
import * as yup from "yup";
import { yupResolver } from "@hookform/resolvers";

const regexUzbNumber = /^(?:\+998)?(?:\d{2})?(?:\d{7})$/;

const acceptFormSchema = yup.object({
  username: yup.string().required("Обязательное поле!"),
  userphone: yup
    .string()
    .matches(regexUzbNumber, "Введите узбекский номер телефона!")
    .required("Обязательное поле!"),
    publicOffer: yup.boolean().required(),
});

export const AcceptPage = () => {
  const { control, handleSubmit } = useForm({
    defaultValues: {
      username: "",
      userphone: "",
      publicOffer: false,
      agreePublicOffer: false,
    },
  });

  const onAcceptSubmit = (data) => {
    console.table(data);
  };
  return (
    <div className="container">
      <div className="wrapper">
        <div className="AcceptPage">
          <Heading
            headingType="h1"
            text="АКЦЕПТ на предложение о заключении договора"
          />
          <form onSubmit={handleSubmit(onAcceptSubmit)}>
            <Controller
              name="username"
              control={control}
              render={({ field }) => (
                <AppInput
                  inputLabel="Ф.И.:"
                  placeholder="Ваш ответ"
                  type="text"
                  {...field}
                />
              )}
            />

            <Controller
              name="userphone"
              control={control}
              render={({ field }) => (
                <AppInput
                  inputLabel="Номер телефона"
                  placeholder="+998 9- --- -- --"
                  type="tel"
                  {...field}
                />
              )}
            />

            <Controller
              name="publicOffer"
              rules={{ required: true }}
              control={control}
              render={({ field }) => (
                <AppCheckbox
                  checkboxLebl="Ознакомился с публичной офертой"
                  {...field}
                />
              )}
            />
            <Controller
              name="agreePublicOffer"
              rules={{ required: true }}
              control={control}
              render={({ field }) => (
                <AppCheckbox
                  checkboxLebl="Согласен с условиями публичной оферты"
                  {...field}
                />
              )}
            />
            <Button buttonType="submit" />
          </form>
        </div>
      </div>
    </div>
  );
};
