import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { Heading } from "../components/Heading";
import { LinkButton } from "../components/LinkButton";
import { Input } from "../components/Input";
import { Controller, useForm } from "react-hook-form";
import * as yup from "yup";
import { yupResolver } from "@hookform/resolvers/yup";

const regexUzbNumber = /^(?:\+998)?(?:\d{2})?(?:\d{7})$/;

const welcomeFormSchema = yup.object({
  username: yup.string().required("Обязательное поле!"),
  userphone: yup
    .string()
    .matches(regexUzbNumber, "Введите узбекский номер телефона!")
    .required("Обязательное поле!"),
  publicOffer: yup.boolean().required("Обязательное поле!"),
  agreePublicOffer: yup.boolean().required("Обязательное поле!"),
});
const Welcome = () => {
  const navigate = useNavigate();

  const [nameValue, setNameValue] = useState("");
  const [phoneValue, setPhoneValue] = useState("");

  const [nameError, setNameError] = useState(false);
  const [phoneError, setPhoneError] = useState(false);

  const {
    control,
    handleSubmit,
    formState: { errors },
  } = useForm({
    resolver: yupResolver(welcomeFormSchema),
    defaultValues: {
      username: "",
      userphone: "",
      publicOffer: false,
      agreePublicOffer: false,
    },
  });

  const goToNextPage = () => {
    if (nameError && phoneError) {
      navigate("/step-one");
    }
  };

  const validateName = () => {
    if (!nameValue) {
      setNameError(true);
    } else {
      setNameError(false);
    }
  };
  const validatePhone = () => {
    if (!phoneValue) {
      setPhoneError(true);
    } else {
      setPhoneError(false);
    }
  };

  const handleNameInput = (value) => {
    setNameValue(value);
    validateName();
  };
  const handlePhoneInput = (value) => {
    setPhoneValue(value);
    validatePhone();
  };

  const clickHandler = () => {
    validateName();
    validatePhone();

    goToNextPage();
  };

  const onWelcomeSubmit = (data) => {
    console.table(data);
  };

  return (
    <div className="container">
      <div className="wrapper">
        <div className="welcome">
          <Heading
            text="Добро пожаловать в квиз от лучшего учебного центра"
            headingType="h1"
          />
          <form onSubmit={handleSubmit(onWelcomeSubmit)}>
            {/* <Input
              hasError={nameError}
              value={nameValue}
              onChange={(value) => handleNameInput(value)}
              id="username"
              isRequired
              inputLabel="Ваше имя"
              inputPlaceholder="Ваш ответ"
              errorMessage="Введите ваше имя"
            /> */}
            {/* <Controller
              name="username"
              control={control}
              render={({ field }) => (
                <AppInput
                  inputLabel="Ваше имя"
                  placeholder="Ваш ответ"
                  type="text"
                  errorMessage={errors.username?.message}
                  hasError={errors.username ? true : false}
                  {...field}
                />
              )}
            /> */}
            <Input
              hasError={phoneError}
              value={phoneValue}
              onChange={(value) => handlePhoneInput(value)}
              id="phone"
              isRequired
              inputLabel="Ваш номер"
              inputPlaceholder="+998 9- --- -- --"
              errorMessage="Введите номер в правильном формате"
            />
            {/* <Controller
              name="userphone"
              control={control}
              render={({ field }) => (
                <AppInput
                  inputLabel="Номер телефона"
                  placeholder="+998 9- --- -- --"
                  type="tel"
                  errorMessage={errors.userphone?.message}
                  hasError={errors.userphone ? true : false}
                  {...field}
                />
              )}
            /> */}
            <LinkButton
              buttonType="button"
              buttonText="Далее"
              onClick={clickHandler}
            />
          </form>
        </div>
      </div>
    </div>
  );
};

export default Welcome;
