import React, { useState } from "react";
import { Heading } from "../components/Heading";
import { Input } from "../components/Input";
import { ProgressBar } from "../components/ProgressBar";
import { LinkButton } from "../components/LinkButton";
import { useNavigate } from "react-router-dom";
import { Controller, useForm } from "react-hook-form";
import * as yup from "yup";
import { yupResolver } from "@hookform/resolvers/yup";
import { AppInput } from "../components/UI/AppInput";
import { Button } from "../components/Button";

const regexUzbNumber = /^(?:\+998)?(?:\d{2})?(?:\d{7})$/;

const stepOneFormSchema = yup.object({
  username: yup.string().required("Обязательное поле!"),
  userphone: yup
    .string()
    .matches(regexUzbNumber, "Введите узбекский номер телефона!")
    .required("Обязательное поле!"),
});

const StepOne = () => {
  const navigate = useNavigate();

  const [answerValue, setPhoneValue] = useState("");
  const [answerError, setPhoneError] = useState(false);

  const {
    control,
    handleSubmit,
    formState: { errors },
  } = useForm({
    resolver: yupResolver(stepOneFormSchema),
    defaultValues: {
      useranswer: "",
    },
  });

  const onStepOneSubmit = (data) => {
    console.table(data);
  };

  const goToNextPage = () => {
    if (answerError) {
      navigate("/step-two");
    }
  };

  const validatePhone = () => {
    if (answerValue) {
      setPhoneError(true);
    } else {
      setPhoneError(false);
    }
  };

  const handlePhoneInput = (value) => {
    setPhoneValue(value);
    validatePhone();
  };

  const clickHandler = () => {
    validatePhone();

    goToNextPage();
  };

  return (
    <div className="container">
      <div className="wrapper">
        <div className="single-input-quiz">
          <ProgressBar currentStep={1} />
          <div className="question">
            <Heading text="1. Занимательный вопрос" headingType="h2" />
            <form onSubmit={handleSubmit(onStepOneSubmit)}>
              <Input
                hasError={phoneError}
                value={phoneValue}
                onChange={(value) => handlePhoneInput(value)}
                isRequired
                type="text"
                id="answer"
                inputPlaceholder="Ваш ответ"
                errorMessage="Введите  в правильном формате"
              />
              {/* <Controller
                name="username"
                control={control}
                render={({ field }) => (
                  <AppInput
                    value={nameValue}
                    onChange={(value) => handleNameInput(value)}
                    id="username"
                    isRequired
                    inputLabel="Ваше имя"
                    inputPlaceholder="Ваш ответ"
                    errorMessage={errors.username?.message}
                    hasError={errors.username ? true : false}
                    {...field}
                  />
                )}
              /> */}
              <LinkButton
                id="next-btn"
                buttonText="Далее"
                onClick={clickHandler}
                path="/step-two"
                type="button"
              />
            </form>
          </div>
        </div>
      </div>
    </div>
  );
};

export default StepOne;
