import React from "react";

export const AppInput = ({
  id,
  inputLabel,
  inputType,
  inputPlaceholder,
  errorMessage,
  hasError,
}) => {
  return (
    <label className={`input-wrapper ${hasError && "_error"}`} htmlFor="{id}">
      {inputLabel}
      <input
        type={inputType}
        placeholder={inputPlaceholder}
      />
      {hasError && <span id="error-message">{errorMessage}</span>}
    </label>
  );
};
